INSERT INTO `email_templates` VALUES (1,'register','User Registration ffff','Welcome','<h2> Welcome @name </h2>\n<p> Thank you for joining @site_name </p>\n<ul>\n  <li>Email: @email</li>\n  <li>Phone: @phone</li>\n</ul>',1,'2025-03-10 01:43:11','2025-07-23 22:17:01'),(2,'password-reset','Password Reset','Reset Your Password for Laravel','<h2>Hello @name,</h2><p>We received a request to reset your password. Use this code:</p><h2>@reset_code</h2><p><i>If this wasn’t you, please ignore this email.</i></p>',1,'2025-03-10 01:43:11','2025-07-23 22:18:19'),(3,'store-creation','New Store Created','A New Store Has Been Created on Laravel','<h1>Hello, @owner_name,</h1>\n                           <p>Your store <strong>@store_name</strong> has been successfully created!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(4,'subscription-expired','Subscription Expired Notification','Your Subscription Has Expired!','<h1>Hello, @owner_name,</h1>\n                           <p>Your subscription for the store <strong>@store_name</strong> has expired on @expiry_date.</p>\n                           <p>Please renew your subscription to continue using our services.</p>',1,'2025-03-10 01:43:11','2025-03-25 00:00:44'),(5,'subscription-renewed','Subscription Renewal Confirmation','Your Subscription Has Been Successfully Renewed!','<h1>Hello, @owner_name,</h1>\n                           <p>Your subscription for the store <strong>@store_name</strong> has been successfully renewed.</p>\n                           <p>New Expiry Date: @new_expiry_date</p>\n                           <p>Thank you for staying with us!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(6,'order-created','New Order Created','You Have a New Order!','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) has been successfully placed.</p>\n                           <p>Order Amount: @order_amount</p>\n                           <p>We will notify you once your order status changes.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(7,'order-created-store','New Order Created for Your Store','You Have a New Order in Your Store!','<h1>Hello @store_owner_name,</h1>\n                           <p>Your store <strong>@store_name</strong> has received a new order (Order ID: @order_id).</p>\n                           <p>Order Amount: @order_amount</p>\n                           <p>Please process the order as soon as possible.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(8,'order-created-admin','New Order Created','New Order Placed on the Platform!','<h1>Hello Admin,</h1>\n                           <p>A new order (Order ID: @order_id) has been placed on the platform.</p>\n                           <p>Order Amount: @order_amount</p>\n                           <p>Please review the order details and take necessary action.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(9,'order-status-pending','Order Pending Notification','Your Order Status: Pending','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) is now <strong>pending</strong>.</p>\n                           <p>We will notify you once the order status changes.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(10,'order-status-confirmed','Order Confirmed Notification','Your Order Status: Confirmed','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) has been <strong>confirmed</strong>!</p>\n                           <p>We will notify you once it is processed.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(11,'order-status-processing','Order Processing Notification','Your Order Status: Processing','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) is now <strong>being processed</strong>.</p>\n                           <p>We will notify you once it is shipped.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(12,'order-status-shipped','Order Shipped Notification','Your Order Status: Shipped','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) has been <strong>shipped</strong>!</p>\n                           <p>It is on its way to you and will arrive soon.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(13,'order-status-delivered','Order Delivered Notification','Your Order Has Been Delivered!','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) has been <strong>delivered</strong>!</p>\n                           <p>We hope you enjoy your purchase!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(14,'order-status-cancelled','Order Cancelled Notification','Your Order Has Been Cancelled','<h1>Hello @customer_name,</h1>\n                           <p>Your order (Order ID: @order_id) has been <strong>cancelled</strong>.</p>\n                           <p>If you have any questions, please contact our support team.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(15,'refund-customer','Refund Processed','Your Refund has Been Processed','<h1>Hello @customer_name,</h1>\n                           <p>Your refund for Order ID: @order_id has been successfully processed.</p>\n                           <p>Refund Amount: @refund_amount</p>\n                           <p>The amount will be credited back to your account shortly.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(16,'refund-store','Refund Processed for Your Store','A Refund has Been Processed for an Order in Your Store','<h1>Hello @store_owner_name,</h1>\n                           <p>A refund has been processed for an order in your store (Order ID: @order_id).</p>\n                           <p>Refund Amount: @refund_amount</p>\n                           <p>Please ensure your account is updated accordingly.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(17,'wallet-balance-added-customer','Customer Wallet Balance Added','Your Wallet Balance Has Been Updated','<h1>Hello @customer_name,</h1>\n                           <p>Your wallet balance has been successfully updated.</p>\n                           <p>New Balance: @balance</p>\n                           <p>Thank you for using our service!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(18,'wallet-balance-added-store','Store Wallet Balance Added','Your Store Wallet Balance Has Been Updated','<h1>Hello @store_owner_name,</h1>\n                           <p>Your store\'s wallet balance has been successfully updated.</p>\n                           <p>Store: @store_name</p>\n                           <p>New Balance: @balance</p>\n                           <p>Thank you for being a part of our platform!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(19,'store-withdrawal-request','Store Withdrawal Request','A Withdrawal Request Has Been Submitted','<h1>Hello Admin,</h1>\n                           <p>A withdrawal request has been submitted by @store_owner_name for their store <strong>@store_name</strong>.</p>\n                           <p>Requested Amount: @amount</p>\n                           <p>Please review and take the necessary action.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(20,'store-withdrawal-approved','Store Withdrawal Approved','Your Withdrawal Request Has Been Approved','<h1>Hello @store_owner_name,</h1>\n                           <p>Your withdrawal request for your store <strong>@store_name</strong> has been approved.</p>\n                           <p>Amount: @amount</p>\n                           <p>The amount will be transferred to your account shortly.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(21,'store-withdrawal-declined','Store Withdrawal Declined','Your Withdrawal Request Has Been Declined','<h1>Hello @store_owner_name,</h1>\n                           <p>Your withdrawal request for your store <strong>@store_name</strong> has been declined.</p>\n                           <p>Amount: @amount</p>\n                           <p>If you have any questions, please contact the support team.</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(22,'deliveryman-withdrawal-request','Deliveryman Withdrawal Request','Your Withdrawal Request Has Been Received','<h1>Hello @deliveryman_name,</h1>\n                       <p>Your withdrawal request has been successfully submitted for the amount of @amount.</p>\n                       <p>Your request is being reviewed by the admin. You will receive a confirmation email once your request has been processed.</p>\n                       <p>Thank you for your hard work!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(23,'deliveryman-earning','Delivery Earnings Notification','You Have New Earnings!','<h1>Hello, @deliveryman_name,</h1>\n                            <p>You\'ve received a new earning:</p>\n                            <p><strong>Order ID:</strong> @order_id</p>\n                            <p><strong>Order Amount:</strong> @order_amount</p>\n                            <p><strong>Earnings:</strong> @earnings_amount</p>\n                            <p>Thank you for your hard work!</p>',1,'2025-03-10 01:43:11','2025-03-10 01:43:11'),(24,'seller-register-for-admin','User Registration','Hello Admin, A New Seller Just Joined BravoMart!','<ul>\n<li><strong>Name:</strong> @name</li>\n<li><strong>Email:</strong> @email</li>\n<li><strong>Phone:</strong> @phone</li>\n</ul>',1,'2025-03-10 01:43:11','2025-07-23 22:19:10');
